function toggleAccordion(e){e.classList.toggle("active");let t=e.nextElementSibling;t.classList.contains("show")?(t.classList.remove("show"),t.style.maxHeight=null):(t.classList.add("show"),t.style.maxHeight=t.scrollHeight+"px")}document.querySelectorAll(".accordion").forEach(e=>{e.addEventListener("click",function(){toggleAccordion(this)})});function openAccordionFromHash(){const e=window.location.hash;if(e){const t=document.querySelector(e);t&&t.classList.contains("accordion")&&(toggleAccordion(t),t.scrollIntoView())}}window.addEventListener("DOMContentLoaded",openAccordionFromHash),window.addEventListener("hashchange",openAccordionFromHash)